mapper.update:
update users
set username = #{username,jdbcType=VARCHAR},
  age = #{age,jdbcType=INTEGER},
  phone = #{phone,jdbcType=BIGINT},
  email = #{email,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, username, age, phone, email
from users
where id = #{id,jdbcType=INTEGER}
